import HttpStatus from 'http-status-codes';
import * as Cartservice from '../services/cart.service'


//add book to cart by id ##############

export const cart = async (req, res, next) => {
    try {
      const data = await Cartservice.cart(req.body.Email, req.params._id);
      res.status(HttpStatus.CREATED).json({
        code: HttpStatus.CREATED,
        data: data,
        message: "The cart is updated"
      });
    } catch (error) {
      res.status(HttpStatus.BAD_REQUEST).json({
        code: HttpStatus.BAD_REQUEST,
        message: `${error}`
      });
    }
  };

  // get all book details from cart ###############

  export const allBooks = async (req, res, next) => {
    try {
      const data = await Cartservice.allBooks(req.body.Email);
      res.status(HttpStatus.OK).json({
        code: HttpStatus.OK,
        data: data,
        message: "Cart details fatched sucessfully"
      });
    } catch (error) {
      res.status(HttpStatus.BAD_REQUEST).json({
        code: HttpStatus.BAD_REQUEST,
        message: `${error}`
      });
    }
  };

  // get all book details from cart ###############

  export const delBook = async (req, res, next) => {
    try {
      const data = await Cartservice.delBook(req.body.Email,req.params._id);
      res.status(HttpStatus.OK).json({
        code: HttpStatus.OK,
        data: data,
        message: "Book deleted sucessfully"
      });
    } catch (error) {
      res.status(HttpStatus.BAD_REQUEST).json({
        code: HttpStatus.BAD_REQUEST,
        message: `${error}`
      });
    }
  };

